import React from "react";
// import { Switch, Redirect, Route } from 'react-router';
import { Routes, Redirect, Route } from 'react-router';
import axios from "axios";
import { Navbar, Container } from "react-bootstrap";
import { BrowserRouter, Link } from "react-router-dom";
import Login from "./login/Login";
import Register from "./login/Register";
import CardPacks from "./CardPacks";
import "./styles.css";
export default class App extends React.Component {
  constructor(props){
    super(props);

    this.authorizeUser = this.authorizeUser.bind(this);
    this.state = {
      loggedIn : false
    }
  }

  componentDidMount() {
       this.authorizeUser();
  }

  authorizeUser() {
    axios.get('http://127.0.0.1:8001/user', {
      headers: {
        'Authorization': localStorage.getItem("tokn")
      }
    })
    .then((res) => {
     console.log(res);
     this.setState({loggedIn : true});
     console.log(this.state.loggedIn);
    })
    .catch((error) => {
      console.error(error);
      this.setState({loggedIn : false});
    })


  }

  render() {
    return (
      <BrowserRouter>

        <NavBar/>
        <Routes>
          <Route exact path="/" element={<Home />}></Route>
          <Route exact path="/login" element={<Login />}></Route>
          <Route exact path="/register" element={<Register />}></Route>
          <Route exact path="/user/cardpacks" element={<CardPacks/>}></Route>
        </Routes>





      </BrowserRouter>
    );
  }
}
class Home extends React.Component {
  render() {
    return (
      <div className="Home">
        {" "}
        <h1>Welcome to the game</h1>

        <Link to="/cardpacks">Open cards</Link>
      </div>
    );
  }
}
class NavBar extends React.Component {
  render() {
    return (
      <div className="NavBar">

      </div>
    );
  }
}
