import "./styles.css";
import React from "react";
import { Navbar, Container } from "react-bootstrap";
import { BrowserRouter, Routes, Route, Link } from "react-router-dom";
import Login from "./login/Login";
import Register from "./login/Register";

export default class App extends React.Component {
  render() {
    return (
      <BrowserRouter>
        <NavBar/>  
        <Routes>
          <Route path="/" element={<Home />}></Route>

          <Route path="/packs" element={<Packs/>}></Route>
          <Route path="/packopening" element={<PackOpening/>}></Route>

          <Route path="/login" element={<Login />}></Route>
          <Route path="/register" element={<Register />}></Route>
        </Routes>
      </BrowserRouter>
    );
  }
}
class Home extends React.Component {
  render() {
    return (
      <div className="Home">
        {" "}
        <h1>Welcome to the game</h1>
        <Link to="/packs">Card Packs</Link>
      </div>
    );
  }
}
class NavBar extends React.Component {
  render() {
    return (
      <nav class="navbar navbar-expand-lg navbar-light bg-light">
      <a class="navbar-brand" href="#">Navbar</a>
      <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
        <span class="navbar-toggler-icon"></span>
      </button>

      <div class="collapse navbar-collapse" id="navbarSupportedContent">
        <ul class="navbar-nav mr-auto">
          <li class="nav-item active">
            <a class="nav-link" href="#">Home <span class="sr-only">(current)</span></a>
          </li>
          <li class="nav-item">
            <a class="nav-link" href="#">Link</a>
          </li>
          <li class="nav-item dropdown">
            <a class="nav-link dropdown-toggle" href="#" id="navbarDropdown" role="button" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
              Dropdown
            </a>
            <div class="dropdown-menu" aria-labelledby="navbarDropdown">
              <a class="dropdown-item" href="#">Action</a>
              <a class="dropdown-item" href="#">Another action</a>
              <div class="dropdown-divider"></div>
              <a class="dropdown-item" href="#">Something else here</a>
            </div>
          </li>
          <li class="nav-item">
            <a class="nav-link disabled" href="#">Disabled</a>
          </li>
        </ul>
        <form class="form-inline my-2 my-lg-0">
          <input class="form-control mr-sm-2" type="search" placeholder="Search" aria-label="Search"/>
          <button class="btn btn-outline-success my-2 my-sm-0" type="submit">Search</button>
        </form>
      </div>
    </nav>
    );
  }
}

class Packs extends React.Component {
  constructor(props) {
    super(props);
    this.state = {

    }
  }

  render () {
    return (<div className = "Packs">

       <button> Buy Packs</button>
              <Link to="/packopening">Open Packs</Link>

    </div>);
  }
}
class PackOpening extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      pack: [],
      packs: 0
    };


    this.openPack = this.openPack.bind(this);
    this.onHover = this.onHover.bind(this);
    this.onLeaveHover = this.onLeaveHover.bind(this);
    this.onClick = this.onClick.bind(this);
  }

  componentDidMount() {
      fetch("http://127.0.0.1:8001/userpacks")
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            packs: result
          });

          if(result <= 0) {
            document.getElementById("open-card-btn").setAttribute("disabled", "disabled");
          }

        },
        (error) => {
          this.setState({
            error
          });
        }
      )

    }

  render() {
    return (
      <div className="PackOpening">

        <button id = "open-card-btn" onClick={this.openPack}>{this.state.packs} Open Card Pack</button>
        <div className="cards-container">
          {this.state.pack.map((i, index) => (
            <div className={`card`}>
              <div
                id={index}
                className={`card-content ${i.rarity}`}
                onMouseEnter={() => this.onHover(index)}
                onMouseLeave={() => this.onLeaveHover(index)}
                onClick={() => this.onClick(index)}
              >
                <div className={`cardframe back`}>
                  <img
                    className="back-icon"
                    src="https://uploads.codesandbox.io/uploads/user/adb5b6ff-3d37-4b52-b8d5-25098bc45d0a/DoYE-back_icon.png
            "
                    width="200px"
                    alt=""
                  />
                </div>

                <div
                  key={index + "Front"}
                  id={index + "Front"}
                  className={`cardframe front ${i.name}`}
                >
                  <div className="cardtop white">
                    {i.rarity} <br />
                    {i.name}
                  </div>
                  <div className="cardmid white">
                    <img
                      className="front-icon"
                      src="https://raw.githubusercontent.com/Bleacheddata/team-willow/4c8078c552029b1113077c393bce2b10f59d87de/src/images/generatorIcon.svg"
                      width="120px"
                      alt=""
                    />
                  </div>

                  <div className="power white"> {i.power} </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  }


  onHover(id) {
    let card = document.getElementById(id);
    card.classList.add("hover");
  }
  onLeaveHover(id) {
    let card = document.getElementById(id);
    card.classList.remove("hover");
  }
  onClick(id) {
    let card = document.getElementById(id);
    card.classList.remove("hover");
    card.classList.add("rotate");
  }

  openPack(event) {


    if(this.state.packs >= 1) {

      fetch("http://127.0.0.1:8001/packopening")
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            pack: result
          });

        },
        (error) => {
          this.setState({
            error
          });
        }
      )
    }
    this.setState({packs : this.state.packs-1})

     if (this.state.packs <= 1) {
      event.target.setAttribute("disabled", "disabled");
    }






  }
}
